<style>
  #witch-svg {
    top: 60px;
    left: 330px;
    height: 253px;
    width: 315px;
    z-index: 6;
    transform: rotate(8deg);
    -webkit-animation: car 14s linear infinite reverse;
    -moz-animation: car 14s linear infinite reverse;
    animation: car 14s linear infinite reverse;
    transition: all .4s ease;
  }
  #light-svg {
    position: inherit;
    left: -470px;
    top: 93px;
    z-index: 9;
    width: 535px;
    height: 235px;
  }
  #front-wheel {
    top: 145px;
    left: -45px;
    position: inherit;
    width: 94px;
  }
  #rear-wheel {
    top: 145px;
    left: 209px;
    position: inherit;
    width: 90px;
  }

  #front-wheel,
  #rear-wheel {
    -webkit-animation-name: rotation;
    -webkit-animation-duration: 5s;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-timing-function: linear;
    -moz-animation-name: rotation;
    -moz-animation-duration: 5s;
    -moz-animation-iteration-count: infinite;
    -moz-animation-timing-function: linear;
    -o-animation-name: rotation;
    -o-animation-duration: 5s;
    -o-animation-iteration-count: infinite;
    -o-animation-timing-function: linear;
    animation-name: rotation;
    animation-duration: 5s;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
  }

  #hair,
  #shoes,
  #hat,
  #gas-tank,
  #rear-tire,
  #front-tire,
  #leg {
    transition: all .4s ease;
  }
  #witch-svg:hover #light {
    transition: all .8s ease;
  }

  #witch-svg:hover #hair {
    stroke: transparent !important;
    fill: url(#hairGradient) !important;
  }
  #witch-svg:hover #leg {
    stroke: transparent !important;
    fill: url(#legGradient) !important;
  }
  #witch-svg:hover #shoes,
  #witch-svg:hover #hat {
    stroke: #343434 !important;
    fill: #343434 !important;
    stroke-width: 0.3 !important;
  }

  #witch-svg:hover #rear-tire,
  #witch-svg:hover #front-tire {
    stroke: #767676 !important;
  }
  #witch-svg:hover #gas-tank {
    fill: url(#gasGradient) !important;
    stroke-width: 0.3 !important;

  }
  #witch-svg:hover #light {
    fill: url(#lightGradient) !important;
  }

  @media (max-width: 950px) {
    #witch-svg {
      top: 23px;
